mapper.select:
select que.question_id as question_id, ans.answer_id as answer_id, que.account as account, que.application as application, que.classify as semantic , que.content as questionContent, que.command as questionCommand, ans.command as answerCommand, ans.content as answerContent from mly_question que left join mly_answer ans on(que.account = ans.account and que.application =ans.application and que.content = ans.question) where que.account = #{robotAccount}
AND que.command = #{questionCommand}
AND (ans.command like concat(que.command, '%') or ans.command is null) AND que.application = #{application} AND (que.classify = #{semantic} or que.classify = concat(#{application},'CORE')) order by que.content,que.create_time,ans.create_time
