mapper.select:
select rob.robot_id as robot_id, rob.account as account, rob.nickname as nickname, rob.species_id as species_id, date_format(rob.birthday,'%Y-%m-%d') as birthday, rob.gender as gender, rob.comefrom as comefrom, rob.hobby as hobby, rob.comment as comment, spe.species_name as species_name from mly_robot rob left join mly_species spe on rob.species_id=spe.species_id where 1=1
and spe.parent_id = #{parentId}
limit #{curpage},#{count}

mapper.delete:

update  mly_robot_scene
set  status=#{status},
where robot_id=#{robotId}  and scene_id=#{sceneId}
