mapper.select:
select rob.robot_id as robot_id, rob.account as account, rob.nickname as nickname, rob.species_id as species_id, rob.birthday as birthday, rob.gender as gender, rob.comefrom as comefrom, rob.hobby as hobby, rob.comment as comment, spe.species_name as species_name from mly_robot rob left join mly_species spe on rob.species_id=spe.species_id
and spe.parent_id = #{parentId}
and spe.parent_id = 0
limit #{curpage},#{count}
